riot.tag2("ftl-breadcrumb",'<div class="ftl-breadcrumb {ftl-breadcrumb-numbered : opts.numbered}"> <a each="{link, index in opts.links}" href="{link.url}" class="{active : link.active}">{link.text} <span if="{link.badge}" class="badge">{link.badge}</span></a> </div>',"","",(function(i){var l=this;linkclicked=function(i){sellink=i.item.link,link={text:sellink.text,url:sellink.url},l.trigger("link-clicked",link)},l.on("before-mount",(function(){l.opts.links||(l.opts.links={links:[]})}))}));