import matplotlib
import matplotlib.pyplot as plt
import random

# Cell

def get_color_list(n=3):
    colors = plt.get_cmap('Set1',n)
    if n<=9:
        return [matplotlib.colors.rgb2hex(colors(i)[:3]).upper() for i in range(colors.N)]
    else:
        return random.sample(list(matplotlib.colors.cnames.values()), n)
