def list_to_r_vector(plist):
    '''

    '''

    r_vector = 'c({to_list})'.format(to_list=','.join([f"'{i}'" for i in plist]))
    return r_vector

# Cell

def dict_to_r_vector(pdict):
    '''

    '''
    p_list = ','.join([f"'{key}'='{value}'" for key,value in pdict.items()])
    r_vector = f'c({p_list})'
    return r_vector


# Cell
def same(x):
    return f"'{x}'"

def dict_to_r_list(pdict,v_f=same):
    '''

    '''
    elements = ',\n'.join([f"{i}={v_f(j)}" for i,j in pdict.items()])
    r_list = f'list({elements})'
    return r_list

