import os
import sys

def create_setup_py(root_dir=None):
    root_dir = root_dir or os.getcwd()
    path = os.path.join(root_dir, 'setup.py')
    with open(path, 'wt', encoding='utf-8') as w:
        w.write('\n'.join([
            '# auto generated by setupmeta_builder',
            '',
            'from setupmeta_builder import setup_it',
            '',
            'setup_it()',
            '',
        ]))

if __name__ == '__main__':
    create_setup_py((sys.argv + [None])[1])
